CREATE TABLE IF NOT EXISTS public.account_types(   id bigint NOT NULL,    name character varying COLLATE pg_catalog."default",    CONSTRAINT account_types_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS public.users(    id bigint NOT NULL,    fullname character varying(255) COLLATE pg_catalog."default",    total_balance bigint,    CONSTRAINT users_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS public.settings(   id bigint NOT NULL,    name character varying COLLATE pg_catalog."default",    CONSTRAINT settings_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS public.accounts(    "number" bigint NOT NULL,    balance numeric(255,2),type bigint,    user_id bigint,    CONSTRAINT accounts_pkey PRIMARY KEY ("number"),    CONSTRAINT fk_type FOREIGN KEY (type)        REFERENCES public.account_types (id) MATCH SIMPLE       ON UPDATE NO ACTION        ON DELETE RESTRICT        NOT VALID,    CONSTRAINT fk_user_id FOREIGN KEY (user_id)        REFERENCES public.users (id) MATCH SIMPLE       ON UPDATE NO ACTION        ON DELETE RESTRICT       NOT VALID);
CREATE TABLE IF NOT EXISTS public.operations(    from_account bigint NOT NULL,    to_account bigint,    sum numeric(255,2),    comment_for_operation character varying(255) COLLATE pg_catalog."default",    id bigint NOT NULL,    CONSTRAINT operations_pkey PRIMARY KEY (id),    CONSTRAINT fk_account_from FOREIGN KEY (from_account)        REFERENCES public.accounts ("number") MATCH SIMPLE        ON UPDATE NO ACTION       ON DELETE RESTRICT        NOT VALID,    CONSTRAINT fk_account_to FOREIGN KEY (to_account)        REFERENCES public.accounts ("number") MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE RESTRICT);
CREATE TABLE IF NOT EXISTS public.operations_applied_settings(    operation_id bigint,    setting_id bigint,    CONSTRAINT fk_operation_id FOREIGN KEY (operation_id)        REFERENCES public.operations (id) MATCH SIMPLE        ON UPDATE NO ACTION       ON DELETE RESTRICT        NOT VALID,    CONSTRAINT fk_setting_id FOREIGN KEY (setting_id)        REFERENCES public.settings (id) MATCH SIMPLE       ON UPDATE NO ACTION       ON DELETE RESTRICT);
CREATE TABLE IF NOT EXISTS public.users_settings(    user_id bigint,    setting_id bigint,    CONSTRAINT fk_settings FOREIGN KEY (setting_id)        REFERENCES public.settings (id) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE RESTRICT        NOT VALID,    CONSTRAINT fk_user_id FOREIGN KEY (user_id)        REFERENCES public.users (id) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE RESTRICT);
